Entity.DisplayName:
var retString = "";
  	  	if(EngineNumber!=null){
  	  	  retString = retString + "Engine " + EngineNumber + ": "
  	  	} else {
  	  	  if(Year==null and Manufacturer==null and Model==null){
  	  	    retString = retString + "Engine"
  	  	  } else {
  	  	    retString = retString + "Engine: "
  	  	  }
  	  	}
  	  	if(Year!=null){
  	  	  retString = retString + Year + " "
  	  	}
  	  	if(Manufacturer!=null){
  	  	  retString = retString + Manufacturer + " "
  	  	}
  	  	if(Model!=null){
  	  	  retString = retString + Model
  	  	}
  	  return retString;
